package constant;

public class ConstantDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a = 2.5;
		System.out.println(a);
		float b = 100000.1f;
		System.out.println(b);
		
		int c = 216541561;
		long d = 43543241345L;
		System.out.println(c);
		System.out.println(d);
		byte e = 100;
		short f = 200;
		

	}

}
