package java문제풀이;


import java.util.Scanner;
public class Chapter3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int i = sc.nextInt();
		
		if (i >0) {
			System.out.println(i);
		}
		else {
			System.out.println(i*-1);
		}

	}

}
