package operator;

public class Operrator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 1 + 2;
		System.out.println(result);
		
		result = result -1;
		System.out.println(result);
		
		result = result * 2;
		System.out.println(result);
		
		result = result / 2;
		System.out.println(result);
		
		result = result +10;
		
		result = result % 7;
		System.out.println(result);
		
		int x, y;
		
		x = 100;
		y = 2000;
		
		System.out.println((x + y)/2);

	}

}
